// Lưu trữ màu sắc RGB
struct Color
{
    std::string character;
    int r, g, b;
    Color() : character("▄"), r(0), g(0), b(0) {}
    Color(std::string character, int r, int g, int b) : character(character), r(r), g(g), b(b) {}
};

// Lưu trữ tọa độ 2D
struct Point
{
    int x, y;
    bool operator==(const Point& other) const { return x == other.x && y == other.y; }
};

// Quản lý điểm cao
struct highScoreEntry {
    std::vector<std::pair<std::string, int>> highScore;
    int score, foodScore, bonusScore, maxHighScore;
    highScoreEntry() : score(0), foodScore(5), bonusScore(25), maxHighScore(5) {}
};
